#requireResource("com.atlassian.gadgets.publisher:ajs-gadgets")
#requireResource("com.atlassian.jira.gadgets:jira-global")
#requireResource("com.atlassian.jira.gadgets:autocomplete")
#requireResource("com.atlassian.jira.gadgets:common")
#requireResource("com.avrethem.issues-metric:issues-metric-resources")
#includeResources()


<script type="text/javascript" src="https://www.gstatic.com/charts/loader.js"></script>
<script type="text/javascript">
console.log("Load Google chart");
google.charts.load('current', {'packages':['corechart']});


(function () {
  var gadget = AJS.Gadget({
      baseUrl: "__ATLASSIAN_BASE_URL__",
      useOauth: "/rest/gadget/1.0/currentUser",
      config: {
        descriptor: function (args) {
            var gadget = this;
            //console.log(args.fav); //Sucessfully loaded favourite filters

            return  {
                fields: [
                {
                    userpref: "searchBox",
                    label: gadget.getMsg("searchBox.label"),
                    description: gadget.getMsg("searchBox.description"),
                    type: "text",
                    value: gadget.getMsg("searchBox.startVal")
                },
                {
                    userpref: "timeUnitsBox",
                    label: gadget.getMsg("timeUnitsBox.label"),
                    description:gadget.getMsg("timeUnitsBox.description"),
                    type: "text",
                    value: gadget.getMsg("timeUnitsBox.startVal")
                },
                {
                    userpref: "timeUnit",
                    label: gadget.getMsg("timeUnit.label"),
                    description:gadget.getMsg("timeUnit.description"),
                    type: "select",
                    selected: gadget.getPref("timeUnit.startVal"),
                    options:[
                    {
                        label:"Day",
                        value:"d"
                    },
                    {
                        label:"Week",
                        value:"w"
                    },
                    {
                        label:"Month",
                        value:"m"
                    },
                    {
                        label:"Year",
                        value:"y"
                    }
                    ]
                },
                AJS.gadget.fields.nowConfigured()
                ]
            };
        }, // End descriptor
        args: [{
            key: "fav",
            ajaxOptions: function() {
             return {
                contentType: 'application/json',
                url: "/rest/api/2/filter/favourite"
            };
        }
    }] // End args
}, // End config
view: {
    enableReload: true,
    onResizeAdjustHeight: true,
    onResizeReload: true,
    template: function(args) {
            // Start template >>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>
            var gadget = this;
                    gadget.getView().empty(); // Reset view

                    var filter = "";
                    if ( gadget.getPref("searchBox") != "" ) {
                        var json = args.favFilters;
                        for (var i = 0; i < json.length; i++) {
                            var element = json[i];

                            if ( element.name == gadget.getPref("searchBox") ) {
                               filter = element.id;
                           }
                       }
                       console.log("Found filter: " + filter);


                 // get filter searchURL and extract JQL-search param from filter
                 var viewURL = "";
                 AJS.$.ajax({
                    url: "/rest/api/2/filter/" + filter,
                    type: 'get',
                    contentType: "application/json",
                    dataType: "json",
                    async: false,
                    success: function(data) { viewURL = data["searchUrl"]; }
                });
                 var jql = viewURL.split("search?jql=")[1];
                 console.log("jql search: " + jql);


                 function drawChart() {
                    var rows = gadget.getPref("timeUnitsBox");
                    var data = new google.visualization.DataTable();
                    data.addColumn('datetime', 'Date');
                    data.addColumn('number', 'Closed');
                    data.addColumn('number', 'Open');


                      var time_period = "d"; // d == day, w == week, y == year


                      for (var i=rows; i >= 0; i--)
                      {
                        var closed = 0;
                        var open = 0;   //      /api/2/searchReq /filter JQL/                                 Issue > startDate                                                 /                   Issue < iterationDate
                        var opened_url = "/rest/api/2/search?jql=" + jql + "%20AND%20createdDate%20>%3D%20startOfDay(-"    + rows.toString() + gadget.getPref("timeUnit") + ")" + "%20AND%20createdDate%20<%3D%20startOfDay(-"    + i.toString() + gadget.getPref("timeUnit") + ")" + "&maxResults=0";
                        var closed_url = "/rest/api/2/search?jql=" + jql + "%20AND%20resolutiondate%20>%3D%20startOfDay(-" + rows.toString() + gadget.getPref("timeUnit") + ")" + "%20AND%20resolutiondate%20<%3D%20startOfDay(-" + i.toString() + gadget.getPref("timeUnit") + ")" + "&maxResults=0";
                        //console.log(opened_url);
                        //console.log(closed_url);
                        AJS.$.ajax({
                            url: opened_url, //+ "%20AND%20status%20%3D%20" + closed_status + "&maxResults=0",
                            type: 'get',
                            contentType: "application/json",
                            dataType: "json",
                            async: false,
                            success: function(data) { open = data["total"];  }
                        });

                        AJS.$.ajax({
                            url: closed_url,// + "%20AND%20status%20%3D%20" + open_status + "&maxResults=0",
                            type: 'get',
                            contentType: "application/json",
                            dataType: "json",
                            async: false,
                            success: function(data) { closed = data["total"]; }
                        });

                        // Add dates to x-Axis
                        var date = new Date();
                        if ( gadget.getPref("timeUnit") == "d" )
                            date.setDate(date.getDate() - i);
                        else if ( gadget.getPref("timeUnit") == "w" )
                            date.setDate(date.getDate() - i*7);
                        else if ( gadget.getPref("timeUnit") == "m" )
                            date.setMonth(date.getMonth() - i);
                        else
                            date.setYear(date.getYear() - i);
                        data.addRow([date, closed, open ]);

                        console.log("Add row:[" +date.getDate() + " | Closed: " + closed + " | Open :" + open + " ]");
                    }


                    var options = {
                      title: 'Open vs Closed issues',
                      curveType: 'none',
                      legend: { position: 'bottom' },
                      lineWidth: 4,
                      hAxis: {
                        title: 'Time'
                    },
                    vAxis: {
                      title: 'Issues'
                  },
              };

              var chart = new google.visualization.LineChart(document.getElementById('chart_div'));

              chart.draw(data, options);
          } // End drawChart()

                    gadget.getView().append("<div id='chart_div' align='center' style='width: 98%; height: 99%'/>"); // Append element to doc, 'curve_chart' set in reports-plugin.js
                    console.log("Call to drawChart()");
                    google.charts.setOnLoadCallback(drawChart);
                    console.log("Completed view statements")

                }


                },// End template <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<
                args: [{
                 key: "favFilters",
                 ajaxOptions: function() {
                     return {
                        contentType: 'application/json',
                        url: "/rest/api/2/filter/favourite" // or /rest/gadget/1.0/pickers/filters?fieldName=
                    };
                }
            }] // End args
        } // End view
    }); // End gadget
})(); // End function
</script>
