                return {
                    contentType: 'application/json',
                    url: "__ATLASSIAN_BASE_URL__/rest/api/latest/status"
                }
            }
            }] // End args
}, // End config
view: {
    enableReload: true,
    onResizeAdjustHeight: true,
            /**
