             *
             * Usage of parameter is: args.'key'
             *
             * @param {JSONArray}    args        A json filled with arguments from args[{...}] section
             * 
             * @return {type} void
             */
             template: function(args) {
                var gadget = this;

            //console.log("Chosen status by name: " + gadget.getPref("statusName"));
            console.log(args.dataset);
            var jsonArray = args.dataset["issues"];                                             // get Json object array from args [{...}] that pulls it from GET-request 

            gadget.getView().empty(); // Reset view

            // Filter comes in format 'filter-filterID'
            var filter = gadget.getPref("filterId").split("filter-")[1];


            var trendData = new google.visualization.DataTable();
            trendData.addColumn('string', 'Date');                                                      
            trendData.addColumn('number', 'Trend');
            var firstTrendPoint = (jsonArray.length > 0) ? (jsonArray[0].total - jsonArray[0].closed) : 0;
            console.log("First trendlinePoint = " + firstTrendPoint);

            var rows = gadget.getPref("timeUnitsBox");
            var chartData = new google.visualization.DataTable();
            chartData.addColumn('string', 'Date');                                                       // Format settings for data rows in chart
            chartData.addColumn('number', gadget.getPref("statusName"));
            chartData.addColumn('number', (gadget.getPref("isCumulative") == "true") ? 'Open' : 'Total');

            var date = new Date(); 
            date.setTime( date.getTime() - new Date().getTimezoneOffset()*60*1000 );                  // Info on Date() https://docs.microsoft.com/en-us/scripting/javascript/reference/date-object-javascript
            //console.log("Today is : " + lastDate.toISOString().substr(0, 19).replace('T', ' '));

            setDateBackwardsInTime(date, gadget.getPref("timeUnitsBox"), gadget.getPref("timeUnit"));
                var firstDate =  new Date(date.getFullYear(), date.getMonth(), date.getDate());       // Get the date where iteration should start



                var open = 0;
                var closed = 0;
                // Loop in acsending dates
                var i = 0;
                while (  i < jsonArray["length"] ) 
                {
                    //console.log("---------------Add Issue to graph----------------");
                    // Issues will be sorted in ascending order 
                    // Loop until date == createdDate for next issue in jsonArray
                    // Compare String, no need to parse to a date? Date.parse("2018-10-15") 
                    while ( date.toISOString().substr(0, 10).replace('T', ' ') < jsonArray[i].issuedate.toString().split(" ")[0] ) 
                    { 
                        if ( (date.getUTCDay() == 6 || date.getUTCDay() == 0) && gadget.getPref("timeUnit") == "d") // Ship 6 == saturday and 0 == Sunday Only when day
                        {
                            date.setDate(date.getDate() +1 );
                            continue; 
                        }

                        /*console.log("issuedate: " + jsonArray[i].issuedate.toString().split(" ")[0]);
                        console.log("Current date: " + date.toISOString().substr(0, 19).replace('T', ' '));//.split(" ")[0]);           
                        console.log("Add row:[" + date.toISOString().substr(0, 19).replace('T', ' ') + " | Closed: " + closed + " | Open :" + open + " ]");
                        console.log("-------------------------------------------------");*/    
                        chartData.addRow([date.toISOString().substr(0, 10), closed, (gadget.getPref("isCumulative") == "true") ? open - closed : open ]);
                        trendData.addRow([date.toISOString().substr(0, 10), (open - closed) - firstTrendPoint]);
                        setDateBackwardsInTime(date, -1, gadget.getPref("timeUnit"));
                    }

                    if ( date.toISOString().substr(0, 10) >= jsonArray[i].issuedate.toString().split(" ")[0] )
                    {

                        open = parseInt(jsonArray[i].total);
                        closed = parseInt(jsonArray[i].closed);
                    }
                    i++;
                }

                // If date have not iterated to todays date
                var now = new Date();
                now.setTime( now.getTime() - new Date().getTimezoneOffset()*60*1000 );
                while ( date.toISOString().substr(0, 10) <= now.toISOString().substr(0, 10) )
                {
                    if ( (date.getUTCDay() == 6 || date.getUTCDay() == 0) && gadget.getPref("timeUnit") == "d") // Ship 6 == saturday and 0 == sunday Only when day
                    {
                        date.setDate(date.getDate() +1 );
                        continue; 
                    }

                    /*console.log("issuedate: " + jsonArray[i].issuedate.toString().split(" ")[0]);
                    console.log("Current date: " + date.toISOString().substr(0, 19).replace('T', ' '));//.split(" ")[0]);           
                    console.log("Add row:[" + date.toISOString().substr(0, 19).replace('T', ' ') + " | Closed: " + closed + " | Open :" + open + " ]");
                    console.log("-------------------------------------------------"); */
                    chartData.addRow([date.toISOString().substr(0, 10), closed, (gadget.getPref("isCumulative") == "true") ? open - closed : open ]);
                    trendData.addRow([date.toISOString().substr(0, 10), (open - closed) - firstTrendPoint]);

                    setDateBackwardsInTime(date, -1, gadget.getPref("timeUnit"));
                }


            /**
             * Prepares dataset and draws it
             *
             * Prepares dataset by adding rows, draws a Google line chart
             *
             *
             * @return {type} void
             */
             function drawChart() {
                    // Google line visual options
                    var chartOptions = {
                      title: 'Open vs ' + gadget.getPref("statusName") + ' issues',
                      isStacked: (gadget.getPref("isCumulative") == "false") ? false : true,
                      curveType: 'none',
                      legend: { position: 'bottom' },
                      lineWidth: 5,
                      hAxis: {
                        //title: 'Time'
                        gridlines: {count: -1},
                        viewWindow: {
                            min: new Date(firstDate.getFullYear(), (firstDate.getMonth()), firstDate.getDate()),
                            max: new Date(new Date().getFullYear(), new Date().getMonth(), new Date().getDate())
                        }
                    },
                    vAxis: {
                        //title: 'Issues',
                        viewWindow: {
                            min: (jsonArray.length > 0) ? jsonArray[0].closed : 0,
                        },
                        format: '#',
                        gridlines: {count: -1}
                    }
                    //chartArea:{ left:'5%',top:'2%',width:'90%',height:'90%'}

                };

                var trendLineOptions = {
                    curveType: 'none',
                    legend: { position: 'none' },
                    vAxis: {
                        format: '#',
                    },
                    hAxis: {
                        //title: 'Time'
                        gridlines: {count: -1},
                        viewWindow: {
                            min: new Date(firstDate.getFullYear(), (firstDate.getMonth()), firstDate.getDate()),
                            max: new Date(new Date().getFullYear(), new Date().getMonth(), new Date().getDate())
                        }
                    }
                    //chartArea:{ left:'5%',top:'2%',width:'90%',height:'900%'}
                }

                var chart = new google.visualization.AreaChart(document.getElementById('chart_div'));
                chart.draw(chartData, chartOptions);

                var trend = new  google.visualization.AreaChart(document.getElementById('trend_chart_div')); 
                trend.draw(trendData, trendLineOptions);

            } // End drawChart()

            gadget.getView().append("<div id='chart_div' align='center' style='width: 98%; height: 65%'/>");    // Append element with id of 'curve_chart' 
            gadget.getView().append("<div id='trend_chart_div' align='center' style='width: 98%; height: 35%'/>");
            google.charts.setOnLoadCallback(drawChart);
            console.log("IssueLinePlotter 2D loaded");

            },// End template function
            args: [{
                key: "dataset",
                ajaxOptions: function() {
                    var d = new Date();
                    d.setTime( d.getTime() - new Date().getTimezoneOffset()*60*1000 );
                    setDateBackwardsInTime(d, this.getPref("timeUnitsBox"), this.getPref("timeUnit"));
                    return {
                        contentType: 'application/json',
                        url: "__ATLASSIAN_BASE_URL__/rest/datagenerator/1.0/message/getJSONdataset",
